import React, { Component } from "react";
import {
  ReactiveBase,
  CategorySearch,
  ResultCard,
  ResultList
} from "@appbaseio/reactivesearch";


class App extends Component {

  onData={
    function(res) {
      return {
        image: "https://www.alliancebhc.org/wp-content/uploads/fotolia_84282280-maquette-maintenance-2.jpg",
        title: res.codice_unico_progetto,
        description: (
            <div>
                <div className="price">${res.importo}</div>
                <p>Comune: {res.comune_progetto} - Provincia: {res.provincia_progetto}</p>
            </div>
        ),
        url: res.uri_lists,
        containerProps: {
          onMouseEnter: () => console.log('ðŸ˜'),
          onMouseLeave: () => console.log('ðŸ™€')
        }
      }
    }
  }

  render() {
    return (
      <ReactiveBase app="operetest" url="http://localhost:9200/" type="doc">
        <CategorySearch
          componentId="searchbox"
          //dataField="descrizione"
          dataField={["descrizione", "forma_giuridica","scelta_contraente"]}
          //categoryField="descrizione.raw"
          placeholder="Cerca per descrizione"
          queryFormat="or"
          fuzziness={0}
          debounce={100}
          react={{
            and: ["CategoryFilter", "SearchFilter"]
          }}
          showFilter={true}
          filterLabel="Venue filter"
          URLParams={false}
        />
       {/*  <ResultCard
          componentId="results"
          dataField="descrizione"
          size={6}
          pagination={true}
          react={{
            and: ["searchbox"]
          }}
          onData={res => {
            return {
              image:
                "https://www.alliancebhc.org/wp-content/uploads/fotolia_84282280-maquette-maintenance-2.jpg",
              title: res.codice_unico_progetto,
              description: res.descrizione
            };
          }}
        /> */}
        <ResultList
          componentId="ResultList01"
          dataField="descrizione"

          react={{
            "and": ["PriceFilter", "SearchFilter"]
          }}
          onData={this.onData}
        />
      </ReactiveBase>
    );
  }
}

export default App;
