import React, { Component } from "react";
import {
  ReactiveBase,
  CategorySearch,
  SingleRange,
  RangeSlider,
  ResultList
} from "@appbaseio/reactivesearch";


class App extends Component {
  render() {
    return (
      <ReactiveBase app="operetest" url="http://localhost:9200/" type="doc">
      	<div style={{ display: "flex", flexDirection: "row" }}>
			<div style={{ display: "flex", flexDirection: "column", width: "40%" }}>
                <CategorySearch
                componentId="searchbox"
                dataField={["descrizione", "forma_giuridica","scelta_contraente"]}
                //categoryField="descrizione.raw"
                placeholder="Cerca per descrizione"
                />
                 <SingleRange
                componentId="ratingsfilter"
                title="Filtra per importo"
                dataField="importo"
                data={[
                    {"start": "50000", "end": "100000", "label": "da 50000 a 100000"},
                    {"start": "10000", "end": "50000", "label": "da 10000 a 50000"},
                    {"start": "5000", "end": "10000", "label": "da 5000 a 10000"},
                    {"start": "2000", "end": "5000", "label": "da 2000 a 5000"},
                    {"start": "1000", "end": "2000", "label": "da 1000 a 2000"},
                    {"start": "1", "end": "1000000", "label": "tutto"},
                ]}
                defaultSelected="tutto"
                /> 
           {/*      <RangeSlider
                    componentId="ratingsfilter"
                    dataField="importo"
                    title="Importo"
                    range={{
                        "start": 1,
                        "end": 1000000
                    }}
                    /> */}
            </div>
            <ResultList
            componentId="results"
            title="Risultati"
            dataField="descrizione"
            from={0}
            size={5}
            pagination={true}
            react={{
                and: ["searchbox", "ratingsfilter"]
            }}
            onData={res => {
                return {
                image:
                    "https://www.alliancebhc.org/wp-content/uploads/fotolia_84282280-maquette-maintenance-2.jpg",
                title: res.codice_unico_progetto,
                description: (
                    <div>
                        <p>{res.descrizione}</p>
                        <div className="price">${res.importo}</div>
                        <p>Comune: {res.comune_progetto} - Provincia: {res.provincia_progetto}</p>
                    </div>
                ),
                };
            }}
            /> 
        </div>
      </ReactiveBase>
    );
  }
}

export default App;
