  render() {
    return (
      <ReactiveBase app="operetest" url="http://localhost:9200/" type="doc">
        <CategorySearch
          componentId="searchbox"
          //dataField="descrizione"
          dataField={["descrizione", "forma_giuridica","scelta_contraente"]}
          //categoryField="descrizione.raw"
          placeholder="Cerca per descrizione"
          queryFormat="or"
          fuzziness={0}
          debounce={100}
          react={{
            and: ["CategoryFilter", "SearchFilter"]
          }}
          showFilter={true}
          filterLabel="Venue filter"
          URLParams={false}
        />

        <ResultList
          componentId="results"
          dataField="descrizione"
          size={6}
          pagination={true}
          react={{
            and: ["searchbox"]
          }}
          onData={res => {
            return {
              image:
                "https://www.alliancebhc.org/wp-content/uploads/fotolia_84282280-maquette-maintenance-2.jpg",
              title: res.codice_unico_progetto,
              description: res.descrizione
            };
          }}
        /> 


       {/*  <ResultCard
